mapper.select:
SELECT 
p.product_id,p.product_name,p.product_desc,p.img_addr,p.normal_price,p.promotion_price,
p.priority,p.create_time,p.last_edit_time,p.enable_status,p.product_category_id,p.shop_id,p.point
FROM tb_product p


		AND product_name LIKE '%${productCondition.productName}%'


		AND enable_status=#{productCondition.enableStatus}


		AND shop_id=#{productCondition.shop.shopId}


		AND product_category_id=#{productCondition.productCategory.productCategoryId}


ORDER BY
priority DESC
LIMIT #{rowIndex},#{pageSize}

mapper.update:
UPDATE tb_product
SET product_category_id = null
WHERE product_category_id=#{productCategory}

mapper.select:
SELECT
COUNT(1)
FROM tb_product


		AND product_name LIKE '%${productCondition.productName}%'


		AND enable_status=#{productCondition.enableStatus}


		AND shop_id=#{productCondition.shop.shopId}


		AND product_category_id=#{productCondition.productCategory.productCategoryId}

mapper.update:
UPDATE tb_product
product_name=#{productName}, product_desc=#{productDesc}, img_addr=#{imgAddr}, normal_price=#{normalPrice}, promotion_price=#{promotionPrice}, priority=#{priority}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, point=#{point}, product_category_id=#{productCategory.productCategoryId},
WHERE shop_id=#{shop.shopId} AND product_id=#{productId}
