mapper.select:
SELECT s.shop_id, s.shop_name, s.shop_desc, s.shop_addr, s.phone, s.shop_img, s.priority, s.create_time, s.last_edit_time, s.enable_status, s.advice, a.area_id, a.area_name, sc.shop_category_id, sc.shop_category_name FROM tb_shop s, tb_shop_category sc, tb_area a
and s.shop_category_id=#{shopCondition.shopCategory.shopCategoryId}
and s.parent_category_id=#{shopCondition.parentCategory.shopCategoryId}
and s.area_id=#{shopCondition.area.areaId}
and s.enable_status=#{shopCondition.enableStatus}
and s.shop_name LIKE '%${shopCondition.shopName}%'
and s.owner_id=#{shopCondition.owner.userId}
AND s.area_id=a.area_id AND s.shop_category_id=sc.shop_category_id
ORDER BY s.priority DESC LIMIT #{rowIndex},#{pageSize}

SELECT
COUNT(1)
FROM
tb_shop s,
tb_shop_category sc,
tb_area a


			and s.shop_category_id=#{shopCondition.shopCategory.shopCategoryId}


			and s.parent_category_id=#{shopCondition.parentCategory.shopCategoryId}


			and s.area_id=#{shopCondition.area.areaId}


			and s.enable_status=#{shopCondition.enableStatus}


			and s.shop_name like '%${shopCondition.shopName}%'


			and s.owner_id=#{shopCondition.owner.userId}

		AND
		s.area_id=a.area_id
		AND 
		s.shop_category_id=sc.shop_category_id

mapper.update:
UPDATE tb_shop
area_id=#{area.areaId}, shop_category_id=#{shopCategory.shopCategoryId}, parent_category_id=#{parentCategory.shopCategoryId}, shop_name=#{shopName}, shop_desc=#{shopDesc}, shop_addr=#{shopAddr}, phone=#{phone}, shop_img=#{shopImg}, longitude=#{longitude}, latitude=#{latitude}, priority=#{priority}, create_time=#{createTime}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, advice=#{advice}
WHERE shop_id=#{shopId}
